mapper.select:
SELECT MID FROM MESSAGE WHERE 1=1
AND FROM_DEL = #{from_del} AND TO_DEL='Y' AND TO_DEL = #{to_del} AND FROM_DEL='Y'
AND MID = #{mid};

mapper.update:

          UPDATE MESSAGE SET
              
              	 FROM_DEL = #{from_del}
              	 TO_DEL = #{to_del}          
                 
           WHERE MID = #{mid}
