mapper.select:
		
          SELECT COUNT(*)
            FROM REPLY
           WHERE POST_PID = ${post_pid}

mapper.insert:
         
            INSERT INTO REPLY ( POST_PID
                              , RP_USER_UID
                              , CONTENTS )
		               VALUES ( ${post_pid}
		                      , ${rp_user_uid}
		                      , #{contents} )

mapper.update:

   UPDATE REPLY SET
          CONTENTS = #{contents},
          USER_UID = #{user_uid} 
    WHERE RID      = ${rid}
