mapper.select:
SELECT * FROM USERS 
       WHERE 1 = 1
       AND LOGINID = #{loginid} AND PASSWORD = #{password} 
       AND UID = #{uid}


  SELECT COUNT(*) AS USERIDCNT 
  FROM USERS
  WHERE LOGINID = #{loginid}


  SELECT COUNT(*) AS USEREMAILCNT 
  FROM USERS
  WHERE EMAIL = #{email}

mapper.update:
 
UPDATE USERS SET
       PASSWORD = #{password},
       EMAIL = #{email},
       NICK = #{nick} 
 WHERE LOGINID = #{loginid}
